// linear node that takes any type. public class LinkedList<T>{	//private variables. 	private LinkedList<T> next;	// element of the type wanted. 	private T element;	// empty constructor sets everything to null 	public LinkedList()	{		next = null;		element = null;	}	//another constructor that assignees the parameter element with it's type to the class's element	public LinkedList (T elem)	{		next = null;		element = elem;	}	// a method that gets the next node. 	public LinkedList<T> getNext()	{		return next;	}	// a method that sets the next node to the node in parameter 	public void setNext (LinkedList<T> node)	{		next = node;	}	// a method that returns the element. 	public T getElement()	{		return element;	}	// a method that sets the element. 	public void setElement (T elem)	{		element = elem;	}}
